import React from 'react';
import { MapContainer, TileLayer, GeoJSON } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import districtData from '../../data/NJCD_2021_ADOPTED_DEC22.json';

function Map() {
    // Style function for the GeoJSON districts
    const districtStyle = {
        color: '#2c3e50', // border color
        weight: 2,        // border width
        fillOpacity: 0.1, // make the fill more transparent
        fillColor: '#3498db'
    };

    // Slightly adjust NJ bounds to better fit the state
    const njBounds = [
        [38.8, -75.6], // SW coordinates adjusted
        [41.4, -73.9]  // NE coordinates adjusted
    ];

    const mapStyle = {
        height: '600px',
        width: '100%',
        borderRadius: '8px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
    };

    return (
        <div className="map-container">
            <MapContainer
                center={[40.1, -74.6]} // Slightly adjusted center
                zoom={8}              // Adjusted zoom level
                style={mapStyle}
                maxBounds={njBounds}
                minZoom={7}
                maxZoom={12}
                boundsOptions={{ padding: [50, 50] }}
            >
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution='&copy; OpenStreetMap contributors'
                />
                <GeoJSON
                    data={districtData}
                    style={districtStyle}
                />
            </MapContainer>
        </div>
    );
}

export default Map;
