import React, { useState } from 'react';
import Map from './components/Map/Map';

function App() {
    const [activeView, setActiveView] = useState('house'); //'house' or 'senate'

    return (
        <div className="min-h-screen bg-gray-100">
            {/* Navigation Bar */}
            <nav className="bg-blue-600 p-4 shadow-lg">
                <div className="container mx-auto flex justify-between items-center">
                    <h1 className="text-2xl font-bold text-white">Find your Vote NJ</h1>

                    {/* Add Search Bar */}
                    <div className="flex items-center">
                        <input
                            type="text"
                            placeholder="Enter your ZIP code"
                            className="px-4 py-2 rounded-l-lg focus:outline-none"
                            maxLength="5"
                            pattern="[0-9]*"
                        />
                        <button className="bg-yellow-500 text-white px-4 py-2 rounded-r-lg hover:bg-yellow-500 ">
                            Find District
                        </button>
                    </div>

                    <div className="flex space-x-6">
                        <a href="/" className="text-yellow-500 hover:text-white transition">Home</a>
                        <a href="/about" className="text-yellow-500 hover:text-white transition">About</a>
                    </div>
                </div>
            </nav>

            {/* Main Content */}
            <div className="flex p-4">
                {/* Left side content */}
                <div className="w-1/2 p-4">
                    {/* District Info */}
                </div>

                {/* Right side - Toggle buttons and map */}
                <div className="w-1/2 p-4">
                    {/* Toggle buttons */}
                    <div className="flex space-x-4 mb-4">
                        <button
                            onClick={() => setActiveView('house')}
                            className={`px-4 py-2 rounded-lg font-medium shadow transition
                                ${activeView === 'house'
                                   ? 'bg-yellow-500 text-white'
                                   : 'bg-white text-blue-600 hover:bg-blue-50'}`}
                        >
                           House Districts
                        </button>
                        <button
                          onClick={() => setActiveView('senate')}
                          className={`px-4 py-2 rounded-lg font-medium shadow transition' +
                            ${activeView === 'senate'
                            ? 'bg-yellow-500 text-white'
                            : 'bg-white text-blue-600 hover:bg-blue-50'}`}
                        >
                            Senate Race
                        </button>
                    </div>

                     {/* Map */}
                     <Map activeView={activeView} />
                </div>
            </div>
        </div>
    );
}

export default App;
