import React, { useState } from 'react';
import { MapContainer, TileLayer, GeoJSON } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import districtData from '../../data/NJCD_2021_ADOPTED_DEC22.json';
import stateBoundaryData from '../../data/State_Boundary_of_NJ%2C_3424.geojson';

function Map({ activeView }) {
    const [selectedDistrict, setSelectedDistrict] = useState(null);

    // Your existing district style
    const districtStyle = (feature) => ({
        color: '#0000FF',
        weight: 2,
        fillOpacity: 0.1,
        fillColor: selectedDistrict === feature.properties.DISTRICT
            ? '#2980b9'
            : '#3498db'
    });

    // New style for state boundary
    const stateStyle = {
        color: '#0000FF',
        weight: 3,
        fillOpacity: 0.05,
        fillColor: '#3498db'
    };

    // Your existing district interaction handlers
    const onEachDistrict = (feature, layer) => {
        layer.on({
            mouseover: (e) => {
                const layer = e.target;
                layer.setStyle({
                    fillOpacity: 0.3
                });
            },
            mouseout: (e) => {
                const layer = e.target;
                layer.setStyle({
                    fillOpacity: 0.1
                });
            },
            click: (e) => {
                setSelectedDistrict(feature.properties.DISTRICT);
            }
        });
    };

    return (
        <div className="map-container">
            <MapContainer
                center={[40.1, -74.6]}
                zoom={8}
                style={{ height: '600px', width: '100%', borderRadius: '8px' }}
                maxBounds={[[38.8, -75.6], [41.4, -73.9]]}
                minZoom={7}
                maxZoom={12}
            >
                <TileLayer
                    url="https://{s}.basemaps.cartocdn.com/light_nolabels/{z}/{x}/{y}{r}.png"
                    attribution='&copy; <a href="http://www.openstreetmap.org/copyright">OpenStreetMap</a>'
                />
                {activeView === 'house' ? (
                    <GeoJSON
                        data={districtData}
                        style={districtStyle}
                        onEachFeature={onEachDistrict}
                    />
                ) : (
                    <GeoJSON
                        data={stateBoundaryData}
                        style={stateStyle}
                    />
                )}
            </MapContainer>
        </div>
    );
}

export default Map;
