import React from 'react';
import { MapContainer, TileLayer, GeoJSON } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import districtData from '../../data/NJCD_2021_ADOPTED_DEC22.json';

function Map() {

    //Defining the NJ state boundaries for the map
    const njBounds = [
        [38.928589, -75.559793], // SW coordinates
        [41.357423, -73.893979]  // NE coordinates
    ];

    const mapStyle = {
        height: '600px',
        width: '100%',
        borderRadius: '8px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
    };

    return (
        <div className="map-container">
            <MapContainer
                center={[40.0583, -74.4057]} // Center of NJ
                zoom={7}
                style={mapStyle}
                maxBounds = {njBounds}
                minZoom={7}
                maxZoom={12}
                boundsOptions={{ padding: [50, 50] }}
                scrollWheelZoom={true}
            >
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution='&copy; OpenStreetMap contributors'
                    noWrap={true}
                    bounds={njBounds}
                />
                <GeoJSON data={districtData} />
            </MapContainer>
        </div>
    );
}

export default Map;
